package patterns;

public class pattern3 {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=4;
		for(int i=0;i<n;i++){
			for(int j=0;j<5;j++){
				if(i>=2 & j<=3){
					System.out.println(" ");
				}else if(i>=3 & j<=3){
					System.out.println(" ");
				}else{
					System.out.println("*");
				}	
				}System.out.println(" ");
		}System.out.println(" ");
	}

}
